import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
// core components
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import Card from "components/Card/Card.js";
import styles from "assets/jss/material-kit-react/views/componentsSections/carouselStyle.js";
import axios from 'axios';
import { useState, useEffect } from 'react';
import Button from '@material-ui/core/Button';
import CardBody from  "components/CardBody/CardBody.js";

const useStyles = makeStyles(styles);

var pageURL = window.location.href;
var lastURLSegment = pageURL.substr(pageURL.lastIndexOf('/') + 1);

export default function SectionCarousel() {

  const [displayurl,setDisplayUrl] = useState();
  const [displayname,setDisplayName] = useState();
  const [displayage,setDisplayAge] = useState();
  const [displayoccupation,setDisplayOccupation] = useState();
  const [displaydescription,setDisplayDescription] = useState();

  //EDIT
  function editTalent(talentID){
    if((displayurl != '')&&(displayname != '')&&(displayage != '')&&(displayoccupation !='')&&(displaydescription !=''))
    {
        axios
        .put('/api/profile/edit-talent/'+talentID,{
            "url": displayurl.toString(),
            "talentName": displayname.toString(), 
            "talentAge": displayage.toString(),
            "talentOccupation": displayoccupation.toString(),
            "talentDescription": displaydescription.toString()
        })
        window.alert('Successfully edited talent!')
        window.location.href = "/afterlogin-page"
    }
    else{
        window.alert('Please input neccessary data!')
    }
  }

  //get specific talent
  function displayOneData(){
    axios
    .get('/api/profile/talent-details'+lastURLSegment)
    .then((response) => {
      console.log(response.data);
    })
    }

    displayOneData();


  const classes = useStyles();

  return (
    <div>
    <h3><b>Edit Talent</b></h3>
    <GridContainer>
    <GridItem xs={12} sm={12} md={12}>
            <Card>
                <CardBody>
                {/* <h4><b>Talent Name :</b></h4>
                <input className="form-control" type="text" defaultValue={storename} onChange={e=>setStoreName(e.target.value)}/> 
                <br></br>
                <h4><b>Talent Age :</b></h4>
                <input className="form-control" type="text" defaultValue={storecode} onChange={e=>setStoreCode(e.target.value)}/>
                <br></br>
                <h4><b>Talent Occupation :</b></h4>
                <input className="form-control" type="text" defaultValue={storeaddress} onChange={e=>setStoreAddress(e.target.value)}/>
                <br></br>
                <h4><b>Talent Description :</b></h4>
                <input className="form-control" type="text" defaultValue={storeaddress} onChange={e=>setStoreAddress(e.target.value)}/> */}
                </CardBody>
            </Card>
        </GridItem>
        <GridItem xs={12} sm={12} md={12}>
            <Button fullWidth color="success">Save</Button> 
        </GridItem>
    </GridContainer>
</div>
  );
}
