import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
// core components
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import Card from "components/Card/Card.js";
import axios from 'axios';
import { useState, useEffect } from 'react';
import Button from '@material-ui/core/Button';
import CardBody from  "components/Card/CardBody.js";
import { TemporaryCredentials } from "aws-sdk";


var pageURL = window.location.href;
var lastURLSegment = pageURL.substr(pageURL.lastIndexOf('/') + 1);

export default function EditBasics() {




  //get specific talent
  const temp=[];
    axios
    .get('/api/profile/onetalent-details/'+lastURLSegment)
    .then((response) => {
      temp.push(response.data)
      console.log(temp);
    })


    //if(temp[0]!=undefined){console.log(temp[0])}

    const [displayurl,setDisplayUrl] = useState(temp[0].url);
    const [displayname,setDisplayName] = useState(temp[0].talentName);
    const [displayage,setDisplayAge] = useState(temp[0].talentAge);
    const [displayoccupation,setDisplayOccupation] = useState(temp[0].talentOccupation);
    const [displaydescription,setDisplayDescription] = useState(temp[0].talentDescription);




  //EDIT
  // function editTalent(talentID){
  //   if((displayurl != '')&&(displayname != '')&&(displayage != '')&&(displayoccupation !='')&&(displaydescription !=''))
  //   {
  //       axios
  //       .put('/api/profile/edit-talent/'+talentID,{
  //           "url": displayurl.toString(),
  //           "talentName": displayname.toString(), 
  //           "talentAge": displayage.toString(),
  //           "talentOccupation": displayoccupation.toString(),
  //           "talentDescription": displaydescription.toString()
  //       })
  //       window.alert('Successfully edited talent!')
  //       window.location.href = "/afterlogin-page"
  //   }
  //   else{
  //       window.alert('Please input neccessary data!')
  //   }
  // }




  return (
    <div>
    <h3><b>Edit Talent</b></h3>
    <GridContainer>
    <GridItem xs={12} sm={12} md={12}>
            <Card>
                <CardBody>
                <h4><b>Image URL :</b></h4>
                <input className="form-control" type="text" defaultValue={displayurl} onChange={e=>setDisplayUrl(e.target.value)}/> 
                <br></br>
                <h4><b>Talent Name :</b></h4>
                <input className="form-control" type="text" defaultValue={displayname} onChange={e=>setDisplayName(e.target.value)}/> 
                <br></br>
                <h4><b>Talent Age :</b></h4>
                <input className="form-control" type="text" defaultValue={displayage} onChange={e=>setDisplayAge(e.target.value)}/>
                <br></br>
                <h4><b>Talent Occupation :</b></h4>
                <input className="form-control" type="text" defaultValue={displayoccupation} onChange={e=>setDisplayOccupation(e.target.value)}/>
                <br></br>
                <h4><b>Talent Description :</b></h4>
                <input className="form-control" type="text" defaultValue={displaydescription} onChange={e=>setDisplayDescription(e.target.value)}/>
                </CardBody>
            </Card>
        </GridItem>
        <GridItem xs={12} sm={12} md={12}>
            <Button fullWidth color="success">Save</Button> 
        </GridItem>
    </GridContainer>
</div>
  );
}
