import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
// core components
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import Card from "components/Card/Card.js";
import axios from 'axios';
import { useState, useEffect } from 'react';
import Button from '@material-ui/core/Button';
import CardBody from  "components/Card/CardBody.js";
import { URL } from "url";
import { forEachTrailingCommentRange } from "typescript";


var pageURL = window.location.href;
var lastURLSegment = pageURL.substr(pageURL.lastIndexOf('/') + 1);

export default function EditBasics() {

  let test = [];
  const [testa, setTesta] = useState("asd");

  async function fetchData() {
    let res = await
      axios
        .get('/api/profile/onetalent-details/'+lastURLSegment);
    let data = await res.data;

    test.push(data)
    //console.log('data')
    //console.log(test[0])
    setTesta(test[0])
  }
  useEffect(() => {
    fetchData();
  }, []); //This will run only once 


  // //get specific talent
  // const temp=[];
  // const [test,setTest] = useState();
  // function displayOneData(){
  //   axios
  //   .get('/api/profile/onetalent-details/'+lastURLSegment)
  //   .then((response) => {
  //     temp.push(response.data[0])
  //     //console.log(temp[0]);
  //     setTest(temp[0]);
  //   })
  //   }

  //   displayOneData();

  console.log(testa[0]);

  const url = testa[0].url;
  const talentname = testa[0].talentName;
  const talentage = testa[0].talentAge;
  const talentoccupation = testa[0].talentOccupation;
  const talentdescription = testa[0].talentDescription;

  const [displayurl,setDisplayUrl] = useState();
  const [displayname,setDisplayName] = useState();
  const [displayage,setDisplayAge] = useState();
  const [displayoccupation,setDisplayOccupation] = useState();
  const [displaydescription,setDisplayDescription] = useState();

  //EDIT
  function editTalent(){
    // if((displayurl != '')&&(displayname != '')&&(displayage != '')&&(displayoccupation !='')&&(displaydescription !=''))
    // {
      if(displayurl=='')
      {
        setDisplayUrl(url);
      }
        axios
        .put('/api/profile/edit-talent/'+lastURLSegment,{
            "url": displayurl,
            "name": displayname, 
            "age": displayage,
            "occupation": displayoccupation,
            "description": displaydescription
        })
        console.log("f");
        console.log(displayage);
        window.alert('Successfully edited talent!')
        window.location.href = "/afterlogin-page"
    //}
    // else{
    //     window.alert('Please input neccessary data!')
    // }
  }




  return (
    <div>
    <h3><b>Edit Talent</b></h3>
    <GridContainer>
    <GridItem xs={12} sm={12} md={12}>
            <Card>
                <CardBody>
                <h4><b>Image URL :</b></h4>
                <input className="form-control" type="text" defaultValue={displayurl} onChange={e=>setDisplayUrl(e.target.value)}/> 
                <br></br>
                <h4><b>Talent Name :</b></h4>
                <input className="form-control" type="text" defaultValue={talentname} onChange={e=>setDisplayName(e.target.value)}/> 
                <br></br>
                <h4><b>Talent Age :</b></h4>
                <input className="form-control" type="text" defaultValue={talentage} onChange={e=>setDisplayAge(e.target.value)}/>
                <br></br>
                <h4><b>Talent Occupation :</b></h4>
                <input className="form-control" type="text" defaultValue={talentoccupation} onChange={e=>setDisplayOccupation(e.target.value)}/>
                <br></br>
                <h4><b>Talent Description :</b></h4>
                <input className="form-control" type="text" defaultValue={talentdescription} onChange={e=>setDisplayDescription(e.target.value)}/>
                </CardBody>
            </Card>
        </GridItem>
        <GridItem xs={12} sm={12} md={12}>
            <Button onClick={editTalent} fullWidth color="success">Save</Button> 
        </GridItem>
    </GridContainer>
</div>
  );
}
