import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
import InputAdornment from "@material-ui/core/InputAdornment";
import Icon from "@material-ui/core/Icon";
// @material-ui/icons
import Email from "@material-ui/icons/Email";
import People from "@material-ui/icons/People";
// core components
import Header from "components/Header/Header.js";
import SubscribeHeader from "components/Header/SubscribeHeader.js";
import Footer from "components/Footer/Footer.js";
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import Button from "components/CustomButtons/Button.js";
import Card from "components/Card/Card.js";
import CardBody from "components/Card/CardBody.js";
import CardHeader from "components/Card/CardHeader.js";
import CardFooter from "components/Card/CardFooter.js";
import CustomInput from "components/CustomInput/CustomInput.js";
import axios from 'axios';
import { useState } from 'react';

import styles from "assets/jss/material-kit-react/views/loginPage.js";

import image from "assets/img/bg7.jpg";
import {auth,db} from "../../Firebase.js";


const useStyles = makeStyles(styles);

export default function SubscribePage(props) {
  const [cardAnimaton, setCardAnimation] = React.useState("cardHidden");
  const [passemail,setPassEmail]= useState("");
  function test(){
    auth.onAuthStateChanged(function(user){
      if(!user){
          window.location.href="/";
      }
      //console.log(user.email);
      db.collection('users').doc(user.uid).get().then(doc=>{
        setPassEmail(user.email);
      })

  })
  console.log(passemail);
  }
  test();

  setTimeout(function () {
    setCardAnimation("");
  }, 700);
  const classes = useStyles();
  const { ...rest } = props;
  return (
    <div>
      <Header
        absolute
        color="transparent"
        brand="TLTT"
        rightLinks={<SubscribeHeader />}
        {...rest}
      />
      <div
        className={classes.pageHeader}
        style={{
          backgroundImage: "url(" + image + ")",
          backgroundSize: "cover",
          backgroundPosition: "top center"
        }}
      >
        <form id="signup-form">
        <div class="w-full mb-2">

            <input
              class="appearance-none border-2 border-gray-200 rounded-md w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-pasha"
              id="email"
              type="email"
              placeholder="Email address"
              required
            />
            </div>

            <button
            id="email-submit"
            class="w-full bg-pasha hover:bg-red-200 hover:shadow-outline rounded-md hover:text-pasha hover:border hover:border-black focus:shadow-outline text-white focus:bg-white focus:text-pasha font-light py-2 px-4 rounded"
            type="submit"
            >
            <div id="loading" class="hidden">Signing up...</div>
            <span id="button-text">Sign up</span>
            </button>
        </form>
       </div>
    </div>
  );
}
