import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
// core components
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import axios from 'axios';
import { useState, useEffect } from 'react';
import Table from "components/Table/Table.js";



import paul from "assets/img/paul.jpg";
import brad from "assets/img/brad.jpg";
import wes from "assets/img/wes.jpg";


import styles from "assets/jss/material-kit-react/views/componentsSections/basicsStyle.js";

const useStyles = makeStyles(styles);

export default function SectionBasics() {

  //DELETE
  function deleteTalent(talentID){
    var answer = window.confirm("Are you sure you want to delete?");
    if(answer){
      axios
     .delete('/api/profile/delete-talent/' + talentID)
     window.location.href = "/afterlogin-page"
    }
    else{
        window.close();
    } 
  }

  //EDIT
  function editTalent(talentID){
    var answer = window.confirm("Are you sure you want to edit?");
    if(answer){
      axios
     .put('/api/profile/edit-talent/' + talentID, {url: "https://assignment2csc.s3.amazonaws.com/bla-1596276364605.JPG",name:'grayson',age:'13',
     occupation: 'dog',description: 'useless'})
     window.location.href = "/afterlogin-page"
    }
    else{
        window.close();
    } 
  }

  let test = [];
  const [testa, setTesta] = useState();

  async function fetchData() {
    let res = await
      axios
        .get('/api/profile/talent-details');
    let data = await res.data;

    test.push(data)
    console.log('data')
    console.log(test[0])
    setTesta(test[0])
  }
  useEffect(() => {
    fetchData();
  }, []); //This will run only once 


  function displaydata() {
    if (testa != undefined) {
      console.log('talentname');
        //console.log(item.talentName)
        //console.log(item.talentOccupation)
        return (
          <Table
              tableHeaderColor="primary"
              tableHead={["Talent Image", "Talent Name", "Talent Age", "Talent occupation", "Talent Description","",""]}
              tableData={
                testa.map((array) => {
                  return [<img height="100px" width="150px" src={array.url}/>,array.talentName,array.talentAge,array.talentOccupation,array.talentDescription,<Button onClick={() => editTalent(array.id)}>Edit</Button>,<Button onClick={() => deleteTalent(array.id)}>Delete</Button>]
                })
              }
            /> 
        )
    }
  }

  const classes = useStyles();
  return (
    <div className={classes.sections}>
      <div className={classes.container}>
        <div className={classes.title}>
          <h4>Recommended for you</h4>
        </div>
        <GridContainer>
        <GridItem xs={12} sm={12} md={4}>
          <Card className={classes.root}>
            <CardActionArea>
              <CardMedia
                component="img"
                height="140"
                className={classes.media}
                image={paul}
                title=""
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="h3">
                  Paul Phua
                </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  Paul Phua is a professional poker player who host Triton Poker competition
                </Typography>
              </CardContent>
            </CardActionArea>
          </Card>
        </GridItem> 

        <GridItem xs={12} sm={12} md={4}>
          <Card className={classes.root}>
            <CardActionArea>
              <CardMedia
                component="img"
                height="140"
                className={classes.media}
                image={brad}
                title=""
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="h3">
                  Brad Owen
                </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  Brad Owen is a professional poker player who host Triton Poker competition
                </Typography>
              </CardContent>
            </CardActionArea>
          </Card>
        </GridItem> 

        <GridItem xs={12} sm={12} md={4}>
          <Card className={classes.root}>
            <CardActionArea>
              <CardMedia
                component="img"
                height="140"
                className={classes.media}
                image={wes}
                title=""
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="h3">
                  Wes Cutshall
                </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  Wes Cutshall is a professional poker player who host Triton Poker competition
                </Typography>
              </CardContent>
            </CardActionArea>
          </Card>
        </GridItem> 
        </GridContainer>
        <br></br>
        <br></br>
        <GridContainer>
          <GridItem xs={12} sm={12} md={4}>

              <Button onClick={event =>  window.location.href='/uploadimage-page'} color="primary">Upload Talent details and image</Button>

          </GridItem>
        </GridContainer>
        <br></br>
        <div className={classes.title}>
          <h4>Talent Details</h4>
        </div>

        {displaydata()}


      </div>
    </div>
  );
}
